import { useState } from 'react'
const Button = (props) => {
  return (
    <button onClick={props.onClick}>
      {props.text}
    </button>
  )
}

//const Button = ({ handleClick, text }) => (  <button onClick={handleClick}>    {text}  </button>)
const Title= ({Title}) => {return (<h1>{Title}</h1>)}
const Buttons=()=> {return (<div> <Button text="Good" onClick={()=>setGood()} /> <Button text="Neutral" onClick={()=>setNeutral()} /> <Button text="Bad" value="bad" onClick={setBad} /> </div>)}
const Rates=()=> {return (<div> <strong>Good: </strong>{good}<br/><strong>Neutral: </strong>{neutral}<br/><strong>Bad: </strong>{bad} </div>)}


let good=0
let bad=0
let neutral=0

const setGood=() => {return (good+1)}
const setNeutral=() => {neutral=neutral+1;console.log("setting neutral:"+neutral); return (neutral)}
const setBad=() => {bad=bad+1;console.log("setting bad:"+bad); return (bad)}

const App = () => {
  // save clicks of each button to its own state
  const [good, setGood] = useState(0)
  const [neutral, setNeutral] = useState(0)
  const [bad, setBad] = useState(0)

  return (
    <div>
      <Title Title="Give Feedback" />
      <Buttons />
      <Title Title="Rate us" />
      <Rates />
    </div>
  )
}

export default App
